mapper.update:
update cart
set count = #{count}
where cart_no =
#{cartNo}

update cart
set count
= #{count}
where member_id = #{memberId}
and product_no = #{productNo}

mapper.select:
select COUNT(1)
from cart
where member_id =
#{memberId}
and product_no = #{productNo}

select COUNT(1)
from cart
where member_id = #{memberId}
