mapper.select:
select member_id, member_pw 
from member 
where member_id = #{memberId} 
and member_pw = #{memberPw}

mapper.update:
update member
set    member_name = #{memberName},
       phone = #{phone},
       email = #{email},
       address  = #{address}
where  member_id = #{memberId}

